<script>
    $(document).ready(function(){
        $('.inputRadio').change(function() {
            $("#select_1").css('background-color', "transparent");
            $("#select_2").css('background-color', "transparent");
            $("#select_3").css('background-color', "transparent");
            $("#select_4").css('background-color', "transparent");

            var seleccion = $(this).val();
            seleccion = seleccion.split(",");

            selectLabel = seleccion[0];
            colorLabel = seleccion[1];

            $("#"+selectLabel).css({
                'background-color': colorLabel
            });
        });
    })
</script>
<script type="text/javascript" src="js/turn/turn.min.js"></script>
<script type="text/javascript">
$(document).ready(function(){

	$("#flipbook_1").turn();
	$("#flipbook_2").turn();
	$("#flipbook_3").turn();
	$("#flipbook_4").turn();
	$("#flipbook_5").turn();
	$("#flipbook_6").turn();

    $('#alfa-pag')[0].addEventListener('click', function() {
        $("#flipbook_1").turn('page', 4);
    });

    $("#flipbook_1").bind("turning", function(event, page, view) {
        stopVideos();
    });
    
});

function stopVideos() {
    $('.youtubeVideo').each(function() {
        var src = $(this).attr('src');
        $(this).attr("src", src);
    });
}

</script>
<script src="https://www.youtube.com/iframe_api"></script>
